function LoadingIndicator({
  message = "載入中⋯⋯",
  centered = false,
  size = "md",
}) {
  const rootClass = centered
    ? "ac-loading-root ac-loading-root-centered"
    : "ac-loading-root";

  // 根據 size prop 決定圖標大小的 class
  const spinnerClass =
    size === "sm" ? "ac-loading-icon ac-loading-icon-sm" : "ac-loading-icon";

  return (
    <div className={rootClass}>
      <div className="ac-loading-spinner-wrapper">
        {/* 使用 SVG 組件替換原本的 div */}
        <LeafIcon className={spinnerClass} />
      </div>
      <div className="ac-loading-text">{message}</div>
    </div>
  );
}

export default LoadingIndicator;
